sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

   var qa = new GlideAjax('GetEmailAddress');
   qa.addParam('sysparm_name', 'getEmail');
   qa.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
   qa.getXML(callback);

   function callback(response) {
      var answerFromXML = response.responseXML.documentElement.getAttribute('answer'); 
      var userObject = JSON.parse(answerFromXML);
      g_form.clearValue('u_requested_for_email'); 
      g_form.setValue('u_requested_for_email', userObject.email);
   } 	
   //Type appropriate comment here, and begin script below

}
